import React, { useState } from 'react';
import './App.css';


function App() {
  // State variables to store form data
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  // Function to handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    const { name, email } = formData;

    if (name === "" || email === "") {
      return;
    }

    var subject = 'Your Quote Request with Attachment';
    var emailBody = `Dear ${name},\n\nThank you for your interest in our products and services. Please find the attached quote. Please ensure to attach the document before sending the email. Should you have any questions or need further assistance, feel free to contact us at rodricksbenjamin@gmail.com.\n\nBest regards,\nΘυροτηλεοράσεις-προσφορές - Eye Security Electronics`;
    var attachment = 'file:///Users/user/Downloads/Cost_Calculator.pdf'; // Replace with the actual path to the attachment on your server

    // Encode special characters in the email body and attachment
    subject = encodeURIComponent(subject);
    emailBody = encodeURIComponent(emailBody);
    attachment = encodeURIComponent(attachment);

    // Construct the Gmail compose URL
    var gmailUrl = `https://mail.google.com/mail/u/0/?view=cm&fs=1&to=${email}&su=${subject}&body=${emailBody}&attcnt=1&attachment=${attachment}`;

    // Open Gmail compose window
    window.open(gmailUrl, '_blank');


    setFormData({
      name: '',
      email: '',
      message: ''
    });
  };

  return (
    <div>
      <header>
        <div className="header-content">
          <img src="support_logo.png" alt="Support Logo" />
          <h1>App Support</h1>
        </div>
      </header>
      <main>
        <section id="faq">
          <h2>Frequently Asked Questions</h2>
          <ul>
            <li><strong>Question:</strong> How do I get started with the app?</li>
            <p><strong>Answer:</strong> To get started, please reach out to WhatsGPS support or your administrator. They will provide you with the necessary credentials to log in and access the app.</p>
            {/* Add more FAQs as needed */}
          </ul>
        </section>
        <section id="contact">
          <h2>Contact Support</h2>
          <form id="contact-form" onSubmit={handleSubmit}>
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} required />
            <label htmlFor="email">Email:</label>
            <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} required />
            <label htmlFor="message">Message:</label>
            <textarea id="message" name="message" rows="4" value={formData.message} onChange={handleChange} required></textarea>
            <button type="submit">Submit</button>
          </form>
        </section>
      </main>
      <footer>
        <p>&copy; 2024 App Support</p>
      </footer>
    </div>
  );
}

export default App;
